function showConfigParams(doCrop, ...
                          useGiaroots_crop, ...
                          doLikelihoodImg, ...
                          doSilhouette_hysteresis, ...
                          useGiaroots_thresholding, ...
                          doSilhouette_harmonic, ...
                          doAxis, ... 
                          doReconstruct_hysteresis, ... 
                          file_ext, ... 
                          file_ext_giaroots_crop, ...      
                          file_ext_giaroots_thresh, ...                          
                          filePrefix, ... 
                          silhouetteForDoAxis, ... 
                          numImgs, ... 
                          numImgUsed, ... 
                          resolution, ... 
                          numNodesOnOctree, ... 
                          sil_upper_threshold, ... 
                          sil_lower_threshold, ... 
                          harmonic_threshold, ...
                          recon_upper_threshold, ... 
                          recon_lower_threshold, ... 
                          distortion_radius, ... 
                          num_components, ...                           
                          recon_opt, ...
                          recon_filename, ...
                          output_file, ...
                          extraInfo, ...
                          ref_image, ...
                          ref_ratio)
                      
%                           pixel_expansion, ... 
%                           ref_image, ...
%                           ref_index, ...
%                           ref_ratio)                      

    disp('---------------------------------------------------');     
    disp('Parameters taken from the configuration file:');  

    fprintf('doCrop=%d',doCrop);
    fprintf('\n');
    fprintf('useGiaroots_crop=%d',useGiaroots_crop);
    fprintf('\n');
    fprintf('doLikelihoodImg=%d',doLikelihoodImg);
    fprintf('\n');
    fprintf('doSilhouette_hysteresis=%d',doSilhouette_hysteresis);
    fprintf('\n');
    fprintf('useGiaroots_thresholding=%d',useGiaroots_thresholding);
    fprintf('\n');
    fprintf('doSilhouette_harmonic=%d',doSilhouette_harmonic);
    fprintf('\n');
    fprintf('doAxis=%d',doAxis);
    fprintf('\n');
    fprintf('doReconstruct_hysteresis=%d',doReconstruct_hysteresis);
    fprintf('\n');
    disp(['file_ext=' file_ext]); 
    disp(['file_ext_giaroots_crop=' file_ext_giaroots_crop]); 
    disp(['file_ext_giaroots_thresh=' file_ext_giaroots_thresh]);     
    disp(['filePrefix=' filePrefix]);     
    fprintf('silhouetteForDoAxis=%d',silhouetteForDoAxis);
    fprintf('\n');
    fprintf('numImgs=%d',numImgs);
    fprintf('\n');
    fprintf('numImgUsed=%d',numImgUsed);
    fprintf('\n'); 
    fprintf('resolution=%d',resolution);
    fprintf('\n');
    fprintf('numNodesOnOctree=%d',numNodesOnOctree);
    fprintf('\n');
    fprintf('sil_upper_threshold=%d',sil_upper_threshold);
    fprintf('\n');
    fprintf('sil_lower_threshold=%d',sil_lower_threshold);
    fprintf('\n');
    fprintf('harmonic_threshold=%d',harmonic_threshold);
    fprintf('\n');
    fprintf('recon_upper_threshold=%d',recon_upper_threshold);
    fprintf('\n');
    fprintf('recon_lower_threshold=%d',recon_lower_threshold);
    fprintf('\n');
    fprintf('distortion_radius=%d',distortion_radius);
    fprintf('\n');
    fprintf('num_components=%d',num_components);
    fprintf('\n');    
    fprintf('recon_opt=%d',recon_opt);
    fprintf('\n');
    disp(['recon_filename=' recon_filename]);  
    disp(['output_file=' output_file]);  
    disp(['extraInfo=' extraInfo]);  

    % before deployment - update Gia-Roots GUI !!!    
    % adding new settings to config.xml
    %fprintf('pixel_expansion=%d',pixel_expansion);
    %fprintf('\n');
    disp(['ref_image=' ref_image]); 
    %fprintf('ref_index=%d',ref_index);
    %fprintf('\n');
    fprintf('ref_ratio=%d',ref_ratio);
    fprintf('\n');
    
    disp('End of parameters taken from the configuration file');
    disp('---------------------------------------------------');
end

